// NOTE: Base64 string is intentionally omitted for brevity in this display.
const YD_LOGO_BASE64 = "data:image/png;base64,";
const SVG_SIGN = "data:image/png;base64,"

const CertificatesPage = () => {
  const { user, isLoading: isAuthLoading } = useAuth();
  const { profile, isLoading: isProfileLoading } = useProfile();
  const { toast } = useToast();
  const [certificates, setCertificates] = useState<CompletedModule[]>([]);
  const [isLoadingData, setIsLoadingData] = useState(true);
  const [isDownloading, setIsDownloading] = useState<string | null>(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [viewingCertificate, setViewingCertificate] = useState<CompletedModule | null>(null);
  const { currentLanguage } = useContext(LanguageContext)!;

  const t = translations[currentLanguage] || translations.english;

  const fetchCompletedModules = async () => {
    if (!user) {
      setIsLoadingData(false);
      return;
    }
    try {
      setIsLoadingData(true);
      const { data, error } = await supabase
        .from('modules_completed')
        .select(`
          module_id,
          completion_date,
          modules!inner (
            name,
            description
          )
        `)
        .eq('user_id', user.id)
        .eq('modules.language', currentLanguage)
        .order('completion_date', { ascending: false });

      if (error) throw error;
      
      const validData = data?.filter(c => c.modules) || [];
      setCertificates(validData as CompletedModule[]);

    } catch (error) {
      console.error("Error fetching certificates:", error);
      toast({
        variant: "destructive",
        title: "Error",
        description: t.errorLoading,
      });
    } finally {
      setIsLoadingData(false);
    }
  };

  useEffect(() => {
    if(!isAuthLoading) {
        fetchCompletedModules();
    }
  }, [user, isAuthLoading, currentLanguage]);

  const handleRefreshCertificates = async () => {
    if (!user) return;
    setIsRefreshing(true);
    try {
        const { data: modules, error: modulesError } = await supabase
            .from('modules')
            .select('id, lessons(id)')
            .eq('language', currentLanguage);

        if (modulesError) throw modulesError;

        const { data: progressData, error: progressError } = await supabase
            .from('user_progress')
            .select('lesson_id')
            .eq('user_id', user.id)
            .eq('status', 'completed');
        
        if (progressError) throw progressError;

        const completedLessonIds = new Set(progressData.map(p => p.lesson_id));

        const newlyCompletedModules = modules.filter(module => {
            const lessonIdsInModule = module.lessons.map(l => l.id);
            return lessonIdsInModule.length > 0 && lessonIdsInModule.every(id => completedLessonIds.has(id));
        });
        
        const newCompletionsToInsert = newlyCompletedModules.map(m => ({
            user_id: user.id,
            module_id: m.id
        }));

        if (newCompletionsToInsert.length > 0) {
            const { error: insertError } = await supabase
                .from('modules_completed')
                .upsert(newCompletionsToInsert, { 
                    onConflict: 'user_id, module_id',
                    ignoreDuplicates: true 
                });
            
            if (insertError) throw insertError;
        }

        toast({
            title: t.refreshComplete,
            description: t.refreshDescription,
        });

        await fetchCompletedModules();

    } catch (error) {
        console.error("Error refreshing certificates:", error);
        toast({
            variant: "destructive",
            title: "Refresh Failed",
            description: "Could not check for new certificates."
        });
    } finally {
        setIsRefreshing(false);
    }
  };

  const handleDownload = async (certificate: CompletedModule) => {
    if (!profile) {
        toast({ title: "Profile not loaded", description: "Please wait for your profile to load before downloading.", variant: "destructive"});
        return;
    }

    setIsDownloading(certificate.module_id);
    
    await new Promise(resolve => setTimeout(resolve, 1000));

    try {
        // 1. Create the jsPDF instance FIRST
        const doc = new jsPDF({
            orientation: 'landscape',
            unit: 'mm',
            format: 'a4'
        });

        // 2. IMPORTANT: Register fonts by passing the 'doc' instance
        // This ensures the fonts are added to THIS specific PDF document.
        registerCustomFonts(doc); 
        
        const primaryColor = '#F97316';
        const navyColor = '#1E293B';   
        const gray = '#64748B';      

        // --- Font Configuration ---
        // Font for content that should always be in a standard English font (like Helvetica)
        const defaultFont = 'helvetica';  
        
        // Font for the module name, which will be conditional based on language
        let customFont_regular = 'helvetica'; 
        let customFont_bold = 'helvetica';
        
        const fontStyleNormal = 'normal';
        const fontStyleBold = 'bold'; 

        // Set customScriptFont (which will be customFont) ONLY if the current language is Hindi or Kannada
        if (currentLanguage === 'hindi') {
          // This MUST EXACTLY match the font family name you identified in your NotoSansDevanagari-normal.js
          customFont_regular = 'NotoSansDevanagari-Regular'; 
          customFont_bold = 'NotoSansDevanagari-Bold';
        } else if (currentLanguage === 'kannada') {
          // This MUST EXACTLY match the font family name you identified in your NotoSansKannada-normal.js
          customFont_regular = 'NotoSansKannada-Regular';   
          customFont_bold = 'NotoSansKannada-Bold';
        }
        
        // --- BEGIN PDF CONTENT ---
        doc.setFillColor(253, 250, 245);
        doc.rect(0, 0, 297, 210, 'F');
        
        doc.setDrawColor(primaryColor);
        doc.setLineWidth(4);
        doc.rect(10, 10, 277, 190);

        // Embed the logo directly using its Base64 representation
        doc.addImage(YD_LOGO_BASE64, 'PNG', 236, 18, 47, 31);

        // Certificate Title - Use customFont
        doc.setFontSize(32);
        doc.setFont(customFont_bold, fontStyleBold);
        doc.setTextColor(navyColor);
        doc.text(t.certificateOfAchievement, 148.5, 50, { align: 'center' });

        // Award text - Use customFont
        doc.setFontSize(16);
        doc.setFont(customFont_regular, fontStyleNormal); 
        doc.setTextColor(gray);
        doc.text(t.thisCertificateIsAwardedTo, 148.5, 75, { align: 'center' });
        
        // User's Name - Use defaultFont (Helvetica), assuming names are Latin characters
        doc.setFontSize(28);
        doc.setFont(defaultFont, fontStyleBold); 
        doc.setTextColor(primaryColor);
        doc.text(profile.name || t.valuedTeamMember, 148.5, 90, { align: 'center' });
        
        // For completing module text - Use customFont
        doc.setFontSize(16);
        doc.setFont(customFont_regular, fontStyleNormal);
        doc.setTextColor(gray);
        doc.text(t.forSuccessfullyCompletingTheModule, 148.5, 110, { align: 'center' });
        
        // Module Name - Conditionally use customScriptFont (customFont) or defaultEnglishFont
        doc.setFontSize(26);
        // Set the font to customFont, which will be the custom font if language is Hindi/Kannada, else Helvetica.
        doc.setFont(customFont_bold, fontStyleBold); 
        doc.setTextColor(navyColor);
        doc.text(certificate.modules.name, 148.5, 125, { align: 'center' });
        
        // Completion Date - Use customFont
        doc.setFontSize(14);
        doc.setFont(customFont_regular, fontStyleNormal);
        doc.setTextColor(gray);
        doc.text(`${t.completedOn}: ${new Date(certificate.completion_date).toLocaleDateString()}`, 148.5, 135, { align: 'center' });
        
        // Signature Image
        doc.addImage(SVG_SIGN, 'PNG', 124, 150, 70, 38);

        // Signature Line and Footer Text - Use customFont
        doc.setLineWidth(0.5);
        doc.setDrawColor(navyColor);
        doc.line(100, 175, 197, 175);
        doc.setFontSize(12);
        doc.setFont(customFont_regular, fontStyleNormal); 
        doc.text(t.svpHeadSales, 148.5, 182, { align: 'center' });
        doc.text(t.prataapSnacksLimited, 148.5, 187, { align: 'center' });
        
        doc.save(`${certificate.modules.name.replace(/\s+/g, '_')}_Certificate.pdf`);
        
        toast({
          title: "Certificate Downloaded",
          description: `Your certificate for "${certificate.modules.name}" has been downloaded.`,
        });

    } catch (error) {
      toast({
        title: "Download Failed",
        description: "There was an error generating your certificate.",
        variant: "destructive"
      });
      console.error("PDF generation error:", error);
    } finally {
      setIsDownloading(null);
    }
  };

  const handleView = (certificate: CompletedModule) => {
    setViewingCertificate(certificate);
  };

  const CertificatePreview = ({ certificate }: { certificate: CompletedModule }) => {
    // Determine font for browser preview.
    let previewFontClass = "font-sans"; // Default, assuming Inter or similar
    if (currentLanguage === 'hindi') {
      previewFontClass = "font-noto-devanagari"; // Custom class for Hindi font in CSS (needs to be defined)
    } else if (currentLanguage === 'kannada') {
      previewFontClass = "font-noto-kannada"; // Custom class for Kannada font in CSS (needs to be defined)
    }

    return (
      <div className={`relative aspect-[297/210] w-full bg-[#fdfaf5] p-[10mm] ${previewFontClass}`}>
        <div className="h-full w-full border-[4mm] border-primary">
          <img src={YD_LOGO_BASE64} alt="Yellow Diamond Logo" className="absolute top-[18mm] right-[22mm] h-[31mm] w-[47mm]" />
          <div className="flex h-full w-full flex-col items-center justify-center text-center">
            <p className="mt-[20mm] text-[32px] font-bold text-yd-navy">{t.certificateOfAchievement}</p>
            <p className="mt-[15mm] text-[16px] text-yd-gray">{t.thisCertificateIsAwardedTo}</p>
            <p className="mt-[3mm] text-[28px] font-bold text-primary">{profile?.name || t.valuedTeamMember}</p>
            <p className="mt-[10mm] text-[16px] text-yd-gray">{t.forSuccessfullyCompletingTheModule}</p>
            <p className="mt-[5mm] text-[26px] font-bold text-yd-navy">{certificate.modules.name}</p>
            <p className="mt-[15mm] text-[14px] text-yd-gray">{t.completedOn}: {new Date(certificate.completion_date).toLocaleDateString()}</p>
            <div className="absolute bottom-[30mm] left-1/2 -translate-x-1/2">
              <div className="h-[0.5mm] w-[77mm] bg-yd-navy"></div>
              <p className="mt-[4mm] text-center text-[12px]">{t.svpHeadSales}</p>
              <p className="text-center text-[12px]">{t.prataapSnacksLimited}</p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const renderContent = () => {
    if (isAuthLoading || isProfileLoading || isLoadingData) {
      return (
        <div className="flex items-center justify-center h-64">
            <Loader2 className="h-12 w-12 animate-spin text-primary" />
        </div>
      );
    }

    if (!user) {
      return (
        <YDCard>
            <div className="p-12 text-center">
                <div className="mx-auto mb-4 flex h-14 w-14 items-center justify-center rounded-full bg-primary text-white">
                    <Award className="h-6 w-6" />
                </div>
                <h2 className="mb-2 text-lg font-semibold text-yd-navy">{t.loginPromptTitle}</h2>
                <p className="mx-auto max-w-md text-sm text-yd-gray">
                   {t.loginPromptDescription}
                </p>
            </div>
        </YDCard>
      );
    }

    if (certificates.length === 0) {
      return (
        <YDCard>
            <div className="p-12 text-center">
                <div className="mx-auto mb-4 flex h-14 w-14 items-center justify-center rounded-full bg-primary text-white">
                    <Award className="h-6 w-6" />
                </div>
                <h2 className="mb-2 text-lg font-semibold text-yd-navy">{t.noCertsTitle}</h2>
                <p className="mx-auto max-w-md text-sm text-yd-gray">
                    {t.noCertsDescription}
                </p>
            </div>
        </YDCard>
      );
    }

    return (
        <div className="space-y-4">
          {certificates.map(cert => (
            <YDCard key={cert.module_id} className="hover:shadow-lg transition-shadow duration-300">
              <YDCardContent className="p-4 flex flex-col sm:flex-row items-start sm:items-center gap-4">
                  <div className="flex h-16 w-16 items-center justify-center rounded-xl bg-gradient-to-br from-primary to-orange-400 text-white shadow-md flex-shrink-0">
                    <Award className="h-8 w-8" />
                  </div>
                  <div className="flex-1">
                      <h3 className="font-bold text-lg text-yd-navy">{cert.modules.name}</h3>
                      <div className="flex items-center text-sm text-muted-foreground mt-1">
                          <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                          <span>{t.completedOn} {new Date(cert.completion_date).toLocaleDateString()}</span>
                      </div>
                  </div>
                  <div className="flex flex-row gap-2 self-start sm:self-center">
                    <YDButton 
                      variant="outline"
                      size="sm"
                      onClick={() => handleView(cert)}
                    >
                      <Eye className="h-4 w-4 mr-1.5" />
                      {t.view}
                    </YDButton>
                    <YDButton 
                      size="sm"
                      onClick={() => handleDownload(cert)}
                      disabled={!!isDownloading}
                    >
                      {isDownloading === cert.module_id ? (
                        <Loader2 className="h-4 w-4 animate-spin" />
                      ) : (
                        <>
                          <Download className="h-4 w-4 mr-1.5" />
                          {t.download}
                        </>
                      )}
                    </YDButton>
                  </div>
              </YDCardContent>
            </YDCard>
          ))}
        </div>
    );
  };

  return (
    <div className="flex h-screen bg-background">
      <Sidebar />
      <div className="flex flex-col flex-1 overflow-hidden">
        <Header />
        <main className="flex-1 overflow-y-auto p-6">
          <div className="yd-container animate-fade-in">
            <div className="flex justify-between items-center mb-6">
                <div>
                    <h1 className="yd-section-title mb-2">{t.title}</h1>
                    <p className="text-muted-foreground">{t.subtitle}</p>
                </div>
                {user && (
                    <YDButton onClick={handleRefreshCertificates} disabled={isRefreshing}>
                        {isRefreshing ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : <RefreshCw className="mr-2 h-4 w-4" />}
                        {isRefreshing ? t.refreshing : t.refresh}
                    </YDButton>
                )}
            </div>
            {renderContent()}
          </div>
        </main>
      </div>
      <Dialog open={!!viewingCertificate} onOpenChange={(open) => !open && setViewingCertificate(null)}>
        <DialogContent className="max-w-5xl p-0 bg-transparent border-0">
          {viewingCertificate && <CertificatePreview certificate={viewingCertificate} />}
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default CertificatesPage;